import { Carousel } from "react-bootstrap";
import {LOGIN_REQUEST,LOGIN_SUCCESS,LOGIN_FAIL,CLEAR_ERRORS,REGISTER_USER_SUCCESS,REGISTER_USER_REQUEST,REGISTER_USER_FAIL,
