import React,{useState,useEffect} from 'react'
import Loader from "../layouts/loader";
import MetaData from '../layouts/MetaData';
import { useAlert } from 'react-alert';
import { useDispatch,useSelector } from 'react-redux';
import { updateProduct,clearErrors,getProductDetails } from '../../actions/productActions';
import { Link } from 'react-router-dom';
import Sidebar from './Sidebar';
import { UPDATE_PRODUCT_RESET } from '../../constants/productConstants';
const UpdateProduct = ({match,history}) => {
    const [name,setName] = useState("");
    const [price,setPrice] = useState(0);
    const [description,setDescription] = useState("");
    const [category,setCategory] = useState("");
    const [stock,setStock] = useState(0);
    const [seller,setSeller] = useState("");
    const [images,setImages] = useState([]);
    const [oldImages,setOldImages] = useState([]);
    const [imagesPreview,setImagesPreview] = useState([]);
    const alert = useAlert();
    const dispatch = useDispatch();
    const {error,product} = useSelector(state=>state.productDetails)

    const {loading,error:updateError,isUpdated} = useSelector(state=>state.deleteProduct);
    const productId = match.params.id;
    // const productId = match.params.id;


    const categories = [
        "Electronics",
        "Cameras",
        "Laptops",
        "Accessories",
        "Headphones",
        "Food",
        "Books",
        "Clothes/Shoes",
        "Beauty/Health",
        "Sports",
        "outdoor",
        "Home"
    ]
    useEffect(() => {
        if(product && product._id!==productId){
            // console.log(product._id,productId);
            // console.log("im here");

            dispatch(getProductDetails(productId));
        }else{
            setName(product.name);
            setPrice(product.price);
            setDescription(product.description);
            setCategory(product.category);
            setSeller(product.seller);
            setStock(product.stock);
            setOldImages(product.images);
        }
        if(error){
         alert.error(error);
         dispatch(clearErrors());
     }
     if(updateError){
        alert.error(updateError);
        dispatch(clearErrors());
     }
     if(isUpdated){
       history.push("/admin/products");
       alert.success("Product Updated Successfully");
       dispatch({type:UPDATE_PRODUCT_RESET})
     }

        // console.log("im in list orders");
     }, [error,alert,isUpdated,history,dispatch,product,productId])
     const submitHandler = (e)=>{
        // console.log("im in submit handler");
        // console.log("seller",seller);
        e.preventDefault();
         const formData = new FormData();
        formData.set("name",name);
        formData.set("price",price);
        formData.set("description",description);
        formData.set("category",category);
        formData.set("stock",stock);
        formData.set("seller",seller);
        images.forEach(image => {
            formData.append('images', image)
        })
        // console.log("formData",formData);
        // console.log("id",product._id)
        dispatch(updateProduct(product._id,formData))
       }
       const onChange = e =>{
        const files = Array.from(e.target.files);
        setImagesPreview([]);
        setImages([]);
        setOldImages([]);
        files.forEach(file=>{
            const reader = new FileReader();
            reader.onload = ()=>{
                if(reader.readyState === 2){
                    // console.log("reader",reader.result);
                    setImagesPreview(oldArray=>[...oldArray,reader.result]);
                    setImages(oldArray=>[...oldArray,reader.result]);
                }
            }
            reader.readAsDataURL(file);
        })
    }
    return (
       <>
        <MetaData title={"Update Product"}></MetaData>
       <div className="row">
         <div className="col-12 col-md-2">
             <Sidebar/>
         </div>
         <div className="col-12 col-md-10">
               <>
               <div className="wrapper my-5"> 
        <form onSubmit={submitHandler} className="shadow-lg" encType='multipart/form-data'>
            <h1 className="mb-4">Update Product</h1>

            <div className="form-group">
              <label htmlFor="name_field">Name</label>
              <input
                type="text"
                id="name_field"
                className="form-control"
                value={name}
                onChange={(e)=>setName(e.target.value)}
              />
            </div>

            <div className="form-group">
                <label htmlFor="price_field">Price</label>
                <input
                  type="text"
                  id="price_field"
                  className="form-control"
                  value={price}
                  onChange={(e)=>setPrice(e.target.value)}
                />
              </div>

              <div className="form-group">
                <label htmlFor="description_field">Description</label>
                <textarea className="form-control" id="description_field" rows="8" value={description} onChange={(e)=>setDescription(e.target.value)}></textarea>
              </div>

              <div className="form-group">
                <label htmlFor="category_field">Category</label>
                <select className="form-control" id="category_field" value={category} onChange={(e)=>setCategory(e.target.value)}>
                {categories.map(category=>(
                    <option key={category} value={category}>{category}</option>
                ))}
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="stock_field">Stock</label>
                <input
                  type="number"
                  id="stock_field"
                  className="form-control"
                  value={stock}
                  onChange={(e)=>setStock(e.target.value)}
                />
              </div>

              <div className="form-group">
                <label htmlFor="seller_field">Seller Name</label>
                <input
                  type="text"
                  id="seller_field"
                  className="form-control"
                  value={seller}
                  onChange={(e)=>setSeller(e.target.value)}
                />
              </div>

              <div className='form-group'>
                <label>Images</label>

                    <div className='custom-file'>
                        <input
                            type='file'
                            name='product_images'
                            className='custom-file-input'
                            id='customFile'
                            onChange={onChange}
                            multiple
                        />
                        <label className='custom-file-label' htmlFor='customFile'>
                            Choose Images
                        </label>
                    </div>

                    {oldImages && oldImages.map(img=>(
                        <img key={img} src={img.url} alt={img.url} className="mt-3 mr-2" width="55" height="52"/>
                    ))}

                    {imagesPreview.map(img=>(
                        <img src={img} key={img} alt="Images Preview" className="mt-3 mr-2" width="55" height="52"/>
                    ))}
            </div>


            <button
              id="login_button"
              type="submit"
              className="btn btn-block py-3"
              disabled={loading?true:false}
            >
              UPDATE
            </button>

          </form>
    </div>

               </>
         </div>
       </div>
       </>
    )
}

export default UpdateProduct
