import { CREATE_ORDER_REQUEST,CREATE_ORDER_SUCCESS,CREATE_ORDER_FAIL,CLEAR_ERRORS,MY_ORDERS_FAIL,MY_ORDERS_REQUEST,MY_ORDERS_SUCCESS,ORDER_DETAILS_FAIL,ORDER_DETAILS_REQUEST,ORDER_DETAILS_SUCCESS,ALL_ORDERS_FAIL,ALL_ORDERS_REQUEST,ALL_ORDERS_SUCCESS,UPDATE_ORDER_FAIL,UPDATE_ORDER_REQUEST,UPDATE_ORDER_SUCCESS,UPDATE_ORDER_RESET,DELETE_ORDER_FAIL,DELETE_ORDER_REQUEST,DELETE_ORDER_SUCCESS,DELETE_ORDER_RESET } from "../constants/orderConstants";
export const newOrderReducer = (state={},action)=>{
   switch(action.type){
       case CREATE_ORDER_REQUEST:
           return {
               ...state,
               loading:true,
            }
        case CREATE_ORDER_SUCCESS:
            return {
                loading:false,
                order:action.payload
            }
        case CREATE_ORDER_FAIL:
            return {
                loading:false,
                error:action.payload
            }
        case CLEAR_ERRORS:
            return {
                ...state,
                error:null
            }
        default:
            return state;
   }

}

export const MyOrdersReducers = (state = { orders: [] },action)=>{
  switch(action.type){
      case MY_ORDERS_REQUEST:
          return {
              ...state,
              loading:true,
        }
        case MY_ORDERS_SUCCESS:
            // console.log("im in order suceess reducer");
            console.log(action.payload);
            return {

                loading:false,
                orders:action.payload
            }
        case MY_ORDERS_FAIL:
            return {
               loading:false,
               error:action.payload
            }
        case CLEAR_ERRORS:
            return {
                ...state,
                error:null
            }
        default :
        return state;

  }
}

export const orderDetailReducer = (state = { order: [] },action)=>{
    switch(action.type){
        case ORDER_DETAILS_REQUEST:
            return {
                ...state,
                loading:true,
          }
          case ORDER_DETAILS_SUCCESS:
            //   console.log("im in order suceess reducer");
              console.log(action.payload);
              return {

                  loading:false,
                  order:action.payload
              }
          case ORDER_DETAILS_FAIL:
              return {
                 loading:false,
                 error:action.payload
              }
          case CLEAR_ERRORS:
              return {
                  ...state,
                  error:null
              }
          default :
          return state;

    }
  }

  export const allOrdersReducer = (state = { orders: [] },action)=>{
    switch(action.type){
        case ALL_ORDERS_REQUEST:
            return {
                ...state,
                loading:true,
          }
          case ALL_ORDERS_SUCCESS:
            //   console.log("im in order suceess reducer");
              console.log(action.payload);
              return {
                  loading:false,
                  orders:action.payload.orders,
                  amount:action.payload.total_amount
              }
          case ALL_ORDERS_FAIL:
              return {
                 loading:false,
                 error:action.payload
              }
          case CLEAR_ERRORS:
              return {
                  ...state,
                  error:null
              }
          default :
          return state;

    }
  }

  export const updateOrderReducer = (state={},action)=>{

    switch(action.type){

        case UPDATE_ORDER_REQUEST:
            case DELETE_ORDER_REQUEST:
            return {
                ...state,
                loading:true,
            }

        case UPDATE_ORDER_SUCCESS:
            return {
                ...state,
                loading:false,
                isUpdated:action.payload
            }
            case DELETE_ORDER_SUCCESS:
        //    console.log("im in delete order case");
           console.log(action.payload)
                return {
                    ...state,
                    loading: false,
                    isDeleted: action.payload
                }

        case UPDATE_ORDER_FAIL:
            case DELETE_ORDER_FAIL:
            return {
                ...state,
                error:action.payload
            }

            case UPDATE_ORDER_RESET:
                return {
                    ...state,
                    isUpdated:false
                }
                case DELETE_ORDER_RESET:
                    return {
                        ...state,
                        isDeleted: false
                    }

            case CLEAR_ERRORS:
                return {
                ...state,
                error:null
             }
        default:
            return state;
    }
}
