export const stripeReducers = (state={},action)=>{
    console.log("im in stripe reducer");
    switch(action.type){
       case `STRIPE_REQUEST`:{
           return {
               ...state,
               loading:true
           }
       }
       case `STRIPE_SUCCESS`:{
        return {
            loading:false,
            stripeApiKey:action.payload
        }
    }
    default:
            return state
    }
}
